package test;

public class Loop {
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int b= 6 ; b >=1 ; b--)  //line
        {
        	 for(int a = 1; a <= b ; a++)  // star
        	 {
        		 System.out.print("*");
        	 }
        	 System.out.println();
        	 
        	 }
        }

}
